import * as React from 'react';
import { AppRegistry, Platform, StyleSheet, View, Image, StatusBar } from 'react-native';
import { Avatar, Button, DefaultTheme, Text, Modal, Portal, Provider as PaperProvider, Snackbar, TextInput, ActivityIndicator } from 'react-native-paper';
import { name as appName } from './app.json';
import magnox_logo from './assets/magnox.png';

const API_URI = "http://localhost:3000";

const theme = {
  ...DefaultTheme,
  roundness: 2,
  colors: {
    ...DefaultTheme.colors,
    placeholder: "#42c5f5", primary: "#42c5f5",
    surface: "#42c5f5",
    accent: '#f1c40f',
  },
};

const styles = Platform.select({
  web: StyleSheet.create({
    login: {
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      flex: 1
    },
    loginPasscodeContainer: {
      paddingLeft: 50,
      paddingRight: 50,
      paddingTop: 250,
      flexDirection: "column",
      justifyContent: "center",
      flex: 1,
      width: 640
    },
    loginButton: {
      marginTop: 10
    },
    loginButtonContent: {
      height: 50
    },
    loginButtonText: {
      color: "#ffffff"
    },
    loginLogo: {
      width: 250,
      height: 250,
      resizeMode: "contain"
    },
    loginLogoContainer: {
      alignItems: "center"
    },
    error: {
      flexDirection: "row",
      backgroundColor: "#f00",
      width: 640,
      alignSelf: "center"
    },
    errorText: {
      color: "#fff",
      flex: 1
    }
  }),
  default: StyleSheet.create({
    login: {
      flexDirection: "column",
      alignItems: "stretch",
      justifyContent: "center",
      flex: 1
    },
    loginPasscodeContainer: {
      paddingLeft: 50,
      paddingRight: 50,
      paddingTop: 250,
      flexDirection: "column",
      justifyContent: "center",
      flex: 1
    },
    loginButton: {
      marginTop: 10
    },
    loginButtonContent: {
      height: 50
    },
    loginButtonText: {
      color: "#ffffff"
    },
    loginLogo: {
      width: 250,
      height: 250,
      resizeMode: "contain"
    },
    loginLogoContainer: {
      alignItems: "center"
    },
    error: {
      flexDirection: "row",
      backgroundColor: "#f00",
    },
    errorText: {
      color: "#fff",
      flex: 1
    },
  })
});

export default class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      login: {
        passcode: "",
      },
      loading: false,
      error: false,
    }
  }
  get(uri = "", body = {}, cb = (obj) => console.dir(obj)) {
    this.setState({ loading: true })
    fetch(
      API_URI + "/" + uri,
      { method: "POST", headers: { "Content-Type": "application/json" }, body: JSON.stringify(body) }
    ).then(res => {
      if (res.status == 200) {
        res.json()
          .then(json => {
            cb(json);
          })
          .catch(err => {
            this.setState({ ...this.state, error: err.message })
            console.log(err);
          })
      }
      else {
        res.json()
          .then(json => {
            this.setState({ ...this.state, error: json.message });
          })
          .catch(err => {
            this.setState({ ...this.state, error: "Error networking!" })
            console.log(err);
          })
      }
    }).catch(err => {
      this.setState({ ...this.state, error: err.message })
      console.log(err);
    }).finally(() => {
      this.setState({ ...this.state, loading: false });
    });
  }
  render() {
    return (
      <PaperProvider>
        <StatusBar />
        <Portal>
          <Modal
            visible={this.state.loading}
            theme={theme}
          >
            <ActivityIndicator />
          </Modal>
        </Portal>
        <View style={styles.login} theme={theme}>
          <View style={styles.loginPasscodeContainer}>
            <TextInput
              label="Test Code"
              style={styles.loginPasscode}
              value={this.state.login.passcode}
              mode="outlined"
              secureTextEntry
              theme={theme}
              onChangeText={text => {
                this.setState({ login: { ...this.state.login, passcode: text } })
                console.log(text);
              }}
              onSubmitEditing={() => this.get("test", { passcode: this.state.login.passcode })}
            />
            <Button
              style={styles.loginButton}
              contentStyle={styles.loginButtonContent}
              labelStyle={styles.loginButtonText}
              mode="contained"
              color="#42c5f5"
              onPress={() => this.get("test", { passcode: this.state.login.passcode })}
              disabled={this.state.loading}
            >Continue</Button>
          </View>
          <View style={styles.loginLogoContainer}>
            <Image style={styles.loginLogo} source={magnox_logo} />
          </View>
        </View>
        <Snackbar
          visible={this.state.error}
          onDismiss={() => {
            this.setState({ error: false });
          }}
          style={styles.error}
          action={{
            label: 'Close',
            onPress: () => this.setState({ error: false })
          }}
          theme={theme}
        >
          <Text style={styles.errorText}>{this.state.error}</Text>
        </Snackbar>
      </PaperProvider >
    );
  }

}


AppRegistry.registerComponent(appName, () => Main);
