import * as React from 'react';
import { AppRegistry, Platform, StyleSheet, View, Image, StatusBar } from 'react-native';
import { Avatar, Button, DefaultTheme, Provider as PaperProvider, Snackbar, TextInput } from 'react-native-paper';
import { name as appName } from './app.json';
import magnox_logo from './assets/magnox.png';

const API_URI = "https://";

const theme = {
  ...DefaultTheme,
  roundness: 2,
  colors: {
    ...DefaultTheme.colors,
    placeholder: "#42c5f5", primary: "#42c5f5",
    surface: "#42c5f5",
    accent: '#f1c40f',
  },
};

const styles = Platform.select({
  default: StyleSheet.create({
    login: {
      flexDirection: "column",
      alignItems: "stretch",
      justifyContent: "center",
      flex: 1
    },
    loginPasscodeContainer: {
      paddingLeft: 50,
      paddingRight: 50,
      paddingTop: 250,
      flexDirection: "column",
      justifyContent: "center",
      flex: 1
    },
    loginButton: {
      marginTop: 10
    },
    loginButtonContent: {
      height: 50
    },
    loginButtonText: {
      color: "#ffffff"
    },
    loginLogo: {
      width: 250,
      height: 250,
      resizeMode: "contain"
    },
    loginLogoContainer: {
      alignItems: "center"
    }
  })
});

export default class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      login: {
        passcode: "",
        buttonLoading: false
      },
      error: false,
    }
  }
  render() {
    return (
      <PaperProvider>
        <StatusBar />
        <View style={styles.login} theme={theme}>
          <View style={styles.loginPasscodeContainer}>
            <TextInput
              label="Test Code"
              style={styles.loginPasscode}
              value={this.state.login.passcode}
              mode="outlined"
              secureTextEntry
              theme={theme}
              onChangeText={text => {
                this.setState({ login: { ...this.state.login, passcode: text } })
                console.log(text);
              }}
            />
            <Button
              style={styles.loginButton}
              contentStyle={styles.loginButtonContent}
              labelStyle={styles.loginButtonText}
              mode="contained"
              color="#42c5f5"
              onPress={e => {
                this.setState({ login: { ...this.state.login, buttonLoading: true } })
                var requestData = { password: this.state.login.passcode };
                var formData = new FormData();
                formData.append("deviceToken", "");
                formData.append("version", "1.0.0");
                formData.append("requestData", JSON.stringify(requestData));
                fetch(
                  API_URI + "user/runTestCan",
                  { method: "POST", headers: { "Content-Type": "application/x-www-form-urlencoded" }, body: formData }
                ).then(res => {
                  console.log(res);
                }).catch(err => {
                  this.setState({ login: { ...this.state.login }, error: err.message })
                  console.log(err);
                }).finally(() => {
                  this.setState({ login: { ...this.state.login, buttonLoading: false } });
                });
              }}
              loading={this.state.login.buttonLoading}
              disabled={this.state.login.buttonLoading}
            >Enter Now</Button>
          </View>
          <View style={styles.loginLogoContainer}>
            <Image style={styles.loginLogo} source={magnox_logo} />
          </View>
        </View>
        <Snackbar
          visible={this.state.error}
          onDismiss={() => {
            this.setState({ error: false });
          }}
          theme={theme}
        >
          {this.state.error}
        </Snackbar>
      </PaperProvider >
    );
  }

}


AppRegistry.registerComponent(appName, () => Main);
