      { method: "POST", headers: { "Content-Type": "application/json" }, body: body ? JSON.stringify(body) : "{}" }
    ).then(res => {
      if (res.status == 200) {
        res.json()
          .then(json => {
            if (cb) cb(json);
          })
          .catch(err => {
            this.setState({ ...this.state, error: err.message })
            console.log(err);
          })
      }
      else {
        res.json()
          .then(json => {
            this.setState({ error: json.message });
          })
          .catch(err => {
            this.setState({ error: "Error networking!" })
            console.log(err);
          })
